	(457,78,'Contact Form','/contact.php','<perch:form template=\"/templates/content/_contact_form.html\" id=\"contact\" method=\"post\" app=\"perch_forms\">\n  <div class=\"form-group\">\n    <perch:label for=\"name\">Name</perch:label>\n    <perch:input type=\"text\" id=\"name\" required=\"true\" label=\"Name\" class=\"form-control\" />\n    <perch:error for=\"name\" type=\"required\">Please add your name</perch:error>\n  </div>\n  <div class=\"form-group\">\n    <perch:label for=\"email\">Email</perch:label>\n    <perch:input type=\"email\" id=\"email\" required=\"true\" label=\"Email\" placeholder=\"you@company.com\" class=\"form-control\" />\n    <perch:error for=\"email\" type=\"required\">Please add your email address</perch:error>\n  </div>\n  <div class=\"form-group\">\n    <perch:label for=\"message\">Message</perch:label>\n    <perch:input type=\"textarea\" id=\"message\" required=\"true\" label=\"Message\" class=\"form-control\" />\n    <perch:error for=\"message\" type=\"required\">Please add a message</perch:error>\n  </div>\n  \n  <perch:input type=\"submit\" id=\"submit\" value=\"Send\" class=\"btn btn-default\" />\n\n  <perch:success>\n    <div class=\"message message-success\">\n      <h2 class=\"message-title\">Thank you for contacting us</h2>\n      <p>We will be in touch as soon as possible. If you need urgent help please use the contact details to the right.</p>\n    </div>\n  </perch:success>\n</perch:form>',0,4,'_contact_form.html',0,'[]',1,1,1,'*'),
